import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import cafe from './producto';


const { useEffect, useState } = require("react");

function Productos() {
    const [productos, setProductos] = useState([]);

    useEffect(() => {
        const URL = "http://localhost:3001/cafes";
        fetch(URL, { headers: { Authorization: 'Bearer {token}' } }).then(data => data.json()).then(data => {
            setProductos(data);
        })
    }, []);

    return (
        <div className="container">
            <h1>Listado de productos</h1>
            <hr></hr>
            <Row>
                {productos.map(producto => <Col><cafe title={producto.id} key={producto.id} /></Col>)}
            </Row>
        </div>)
}

export default Productos;
