import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Details from './details';
import Container from 'react-bootstrap/Container';
import Cafe from './producto';
import Card from 'react-bootstrap/Card';
import fondo from '../images/fondo.jpg';
import { useParams } from "react-router-dom";
import Table from 'react-bootstrap/Table';
import { useState } from "react";

const { useEffect } = require("react");

function Productos() {
    const [productos, setProductos] = useState([]);
    const params = useParams();
    useEffect(() => {
        const URL = "http://localhost:3001/cafes";
        fetch(URL).then(data => data.json()).then(data => {
            setProductos(data);
        })
    }, []);

    // const params = useParams();
    const [product, setProduct] = useState([]);
    useEffect(() => {
        const URL = "http://localhost:3001/cafes/" + params.id;
        fetch(URL).then(data => data.json()).then(data => {
            setProduct(data);
        })
    }, []);

    return (
        <Container>
            <h1>El aroma mágico</h1>
            <Row className="justify-content-md-center">
                <img
                    src={fondo}
                    alt="El aroma mágico"
                    width="80%"
                />
            </Row>
            <Row >
                <Col>
                    <div className="container">
                        <table className="table">
                            <thead className={"table-dark"}>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Tipo</th>
                                    <th scope="col">Región</th>
                                </tr>
                            </thead>
                            <tbody>
                                {productos.map((e, i) => (<Cafe key={i} title={e} />))}
                            </tbody>
                        </table>
                    </div>
                </Col>
                <Col>

                    <Card style={{ width: '18rem' }} className="mb-3" >
                        <Card.Body>
                            <Card.Text>
                                {product.nombre}
                            </Card.Text>
                            <Card.Text>
                                {product.fecha_cultivo}
                            </Card.Text>
                            <img
                                src={product.imagen}
                                width="80%"
                            />
                            <Card.Text>
                                Notas
                            </Card.Text>
                            <Card.Text>
                                {product.notas}

                            </Card.Text>
                            <Card.Text>
                                Cultivado a la altura de {product.altura} msnm
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>



        </Container >

    )
}
